function openTab(e,t){for(var a,l=document.getElementsByClassName("product__tabcontent"),n=0;n<l.length;n++)l[n].style.display="none";for(a=document.getElementsByClassName("product__tablink"),n=0;n<a.length;n++)a[n].className=a[n].className.replace(" active","");document.getElementById(t).style.display="block",e.currentTarget.className+=" active"}document.getElementById("defaultOpen").click();